import type { Express } from "express";
import { createServer, type Server } from "http";
import { setupAuth } from "./auth";
import { storage } from "./storage";
import { insertPrizeSchema, insertParticipantSchema, insertSubmissionSchema, insertSettingsSchema, insertStoreAddressSchema, insertProductCatalogSchema } from "@shared/schema";

export function registerRoutes(app: Express): Server {
  // Setup authentication routes
  setupAuth(app);

  // Settings routes
  app.get("/api/settings", async (req, res) => {
    try {
      const settings = await storage.getSettings();
      res.json(settings);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch settings" });
    }
  });

  app.put("/api/settings", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const validatedData = insertSettingsSchema.parse(req.body);
      const settings = await storage.updateSettings(validatedData);
      res.json(settings);
    } catch (error) {
      res.status(400).json({ message: "Invalid settings data" });
    }
  });

  // Prize routes
  app.get("/api/prizes", async (req, res) => {
    try {
      const prizes = await storage.getAllPrizes();
      res.json(prizes);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch prizes" });
    }
  });

  app.post("/api/prizes", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const data = req.body;
      // Convert string dates to Date objects
      const processedData = {
        ...data,
        startDate: new Date(data.startDate),
        endDate: new Date(data.endDate),
      };

      const validatedData = insertPrizeSchema.parse(processedData);
      const prize = await storage.createPrize(validatedData);
      res.status(201).json(prize);
    } catch (error) {
      console.error('Error creating prize:', error);
      res.status(400).json({ 
        message: "Invalid prize data", 
        error: error instanceof Error ? error.message : 'Unknown error' 
      });
    }
  });

  app.put("/api/prizes/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { id } = req.params;
      const data = req.body;

      // Convert string dates to Date objects if present
      const processedData = { ...data };
      if (data.startDate) processedData.startDate = new Date(data.startDate);
      if (data.endDate) processedData.endDate = new Date(data.endDate);

      const updates = insertPrizeSchema.partial().parse(processedData);
      const prize = await storage.updatePrize(id, updates);
      res.json(prize);
    } catch (error) {
      res.status(400).json({ message: "Failed to update prize" });
    }
  });

  app.delete("/api/prizes/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { id } = req.params;
      const success = await storage.deletePrize(id);
      if (success) {
        res.status(204).send();
      } else {
        res.status(404).json({ message: "Prize not found" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete prize" });
    }
  });

  // Participant routes
  app.get("/api/participants", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { prizeName } = req.query;
      const participants = await storage.getAllParticipants();

      // Filter by prize name if specified
      const filteredParticipants = prizeName 
        ? participants.filter(p => p.prizeName && p.prizeName.toLowerCase().includes((prizeName as string).toLowerCase()))
        : participants;

      res.json(filteredParticipants);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch participants" });
    }
  });

  app.post("/api/participants", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const validatedData = insertParticipantSchema.parse(req.body);
      const participant = await storage.createParticipant(validatedData);
      res.status(201).json(participant);
    } catch (error) {
      res.status(400).json({ message: "Invalid participant data" });
    }
  });

  app.put("/api/participants/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { id } = req.params;
      const updates = insertParticipantSchema.partial().parse(req.body);
      const participant = await storage.updateParticipant(id, updates);
      res.json(participant);
    } catch (error) {
      res.status(400).json({ message: "Failed to update participant" });
    }
  });

  app.delete("/api/participants/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { id } = req.params;
      const success = await storage.deleteParticipant(id);
      if (success) {
        res.status(204).send();
      } else {
        res.status(404).json({ message: "Participant not found" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete participant" });
    }
  });

  // Coupon checking route (public)
  app.post("/api/check-coupon", async (req, res) => {
    try {
      const submissionData = insertSubmissionSchema.parse(req.body);

      // Check if coupon exists as a prize coupon number
      const prizes = await storage.getAllPrizes();
      const currentDate = new Date();

      const matchingPrize = prizes.find(prize => 
        prize.couponNumber === submissionData.couponNumber && 
        prize.isActive &&
        currentDate >= new Date(prize.startDate) &&
        currentDate <= new Date(prize.endDate)
      );


      let isWinner = false;
      let prizeId = null;
      let prizeName = null;
      let prizeBanner = null;

      if (matchingPrize) {
        isWinner = true;
        prizeId = matchingPrize.id;
        prizeName = matchingPrize.name;
        prizeBanner = matchingPrize.bannerUrl;
      }

      // Save submission to history
      const submission = await storage.createSubmission({
        ...submissionData,
        isWinner,
        prizeId,
        prizeName,
      });

      // Create participant entry if winner or first submission for this coupon
      const existingParticipant = await storage.getParticipantByCoupon(submissionData.couponNumber);
      if (!existingParticipant) {
        await storage.createParticipant({
          couponNumber: submissionData.couponNumber,
          fullName: submissionData.fullName,
          whatsappNumber: submissionData.whatsappNumber,
          isWinner,
          prizeId,
          prizeName,
          isPrizeClaimed: false,
        });
      }

      res.json({
        isWinner,
        prizeName,
        prizeBanner,
        message: isWinner 
          ? (await storage.getSettings())?.winnerMessage || "Selamat! Anda mendapat hadiah!"
          : "Maaf, nomor kupon Anda tidak mendapatkan hadiah kali ini."
      });
    } catch (error) {
      res.status(400).json({ message: "Invalid submission data" });
    }
  });

  // Submissions routes
  app.get("/api/submissions", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const { search } = req.query;
      const submissions = search 
        ? await storage.searchSubmissions(search as string)
        : await storage.getAllSubmissions();
      res.json(submissions);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch submissions" });
    }
  });

  // Store Address routes
  app.get("/api/store-addresses", async (req, res) => {
    try {
      const storeAddresses = await storage.getAllStoreAddresses();
      res.json(storeAddresses);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch store addresses" });
    }
  });

  app.post("/api/store-addresses", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const validatedData = insertStoreAddressSchema.parse(req.body);
      const storeAddress = await storage.createStoreAddress(validatedData);
      res.status(201).json(storeAddress);
    } catch (error) {
      res.status(400).json({ message: "Invalid store address data" });
    }
  });

  app.put("/api/store-addresses/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const storeAddress = await storage.updateStoreAddress(req.params.id, req.body);
      res.json(storeAddress);
    } catch (error) {
      res.status(400).json({ message: "Failed to update store address" });
    }
  });

  app.delete("/api/store-addresses/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const success = await storage.deleteStoreAddress(req.params.id);
      if (success) {
        res.json({ message: "Store address deleted successfully" });
      } else {
        res.status(404).json({ message: "Store address not found" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete store address" });
    }
  });

  // Product Catalog routes
  app.get("/api/products", async (req, res) => {
    try {
      const products = await storage.getAllProducts();
      res.json(products);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch products" });
    }
  });

  app.post("/api/products", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const validatedData = insertProductCatalogSchema.parse(req.body);
      const product = await storage.createProduct(validatedData);
      res.status(201).json(product);
    } catch (error) {
      res.status(400).json({ message: "Invalid product data" });
    }
  });

  app.put("/api/products/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const product = await storage.updateProduct(req.params.id, req.body);
      res.json(product);
    } catch (error) {
      res.status(400).json({ message: "Failed to update product" });
    }
  });

  app.delete("/api/products/:id", async (req, res) => {
    if (!req.isAuthenticated()) {
      return res.status(401).json({ message: "Authentication required" });
    }

    try {
      const success = await storage.deleteProduct(req.params.id);
      if (success) {
        res.json({ message: "Product deleted successfully" });
      } else {
        res.status(404).json({ message: "Product not found" });
      }
    } catch (error) {
      res.status(500).json({ message: "Failed to delete product" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

