import { Switch, Route, useLocation } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider } from "@/hooks/use-auth";
import HomePage from "@/pages/home-page";
import AdminDashboard from "@/pages/admin-dashboard";
import AuthPage from "@/pages/auth-page";
import NotFound from "@/pages/not-found";
import StoreAddressesPage from "@/pages/store-addresses";
import ProductCatalogPage from "@/pages/product-catalog";
import { ProtectedRoute } from "./lib/protected-route";
import Navbar from "@/components/navbar";

function Router() {
  const [location] = useLocation();
  const showNavbar = !location.startsWith('/admin') && !location.startsWith('/auth');

  return (
    <div className="min-h-screen bg-gray-50">
      {showNavbar && <Navbar />}
      <main>
        <Switch>
          <Route path="/" component={HomePage} />
          <Route path="/auth" component={AuthPage} />
          <Route path="/alamat-toko" component={StoreAddressesPage} />
          <Route path="/katalog-produk" component={ProductCatalogPage} />
          <ProtectedRoute path="/admin" component={AdminDashboard} />
          <Route component={NotFound} />
        </Switch>
      </main>
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;

