import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Settings, InsertSettings } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Save, FileText } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function PlaceholderManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState<InsertSettings>({});

  const { data: settings, isLoading } = useQuery<Settings>({
    queryKey: ["/api/settings"],
  });

  useEffect(() => {
    if (settings) {
      setFormData({
        siteTitle: settings.siteTitle,
        logoUrl: settings.logoUrl,
        bannerUrl: settings.bannerUrl,
        adminWhatsApp: settings.adminWhatsApp,
        mapsLink: settings.mapsLink,
        termsAndConditions: settings.termsAndConditions,
        winnerMessage: settings.winnerMessage,
        couponPlaceholder: settings.couponPlaceholder,
        namePlaceholder: settings.namePlaceholder,
        whatsappPlaceholder: settings.whatsappPlaceholder,
      });
    }
  }, [settings]);

  const updateMutation = useMutation({
    mutationFn: async (data: InsertSettings) => {
      const res = await apiRequest("PUT", "/api/settings", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Berhasil",
        description: "Placeholder berhasil diperbarui",
      });
    },
    onError: (error) => {
      toast({
        title: "Kesalahan",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateMutation.mutate(formData);
  };

  if (isLoading) {
    return <div>Memuat...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <FileText className="mr-2 h-5 w-5" />
          Kelola Placeholder Teks
        </CardTitle>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="couponPlaceholder">Placeholder Nomor Kupon</Label>
            <Input
              id="couponPlaceholder"
              type="text"
              value={formData.couponPlaceholder || ""}
              onChange={(e) => setFormData(prev => ({ ...prev, couponPlaceholder: e.target.value }))}
              placeholder="Tulis Nomor kuponmu disini"
              data-testid="input-coupon-placeholder"
            />
          </div>

          <div>
            <Label htmlFor="namePlaceholder">Placeholder Nama Lengkap</Label>
            <Input
              id="namePlaceholder"
              type="text"
              value={formData.namePlaceholder || ""}
              onChange={(e) => setFormData(prev => ({ ...prev, namePlaceholder: e.target.value }))}
              placeholder="Nama Lengkap"
              data-testid="input-name-placeholder"
            />
          </div>

          <div>
            <Label htmlFor="whatsappPlaceholder">Placeholder Nomor WhatsApp</Label>
            <Input
              id="whatsappPlaceholder"
              type="text"
              value={formData.whatsappPlaceholder || ""}
              onChange={(e) => setFormData(prev => ({ ...prev, whatsappPlaceholder: e.target.value }))}
              placeholder="Nomor Whatsapp"
              data-testid="input-whatsapp-placeholder"
            />
          </div>

          <Button
            type="submit"
            disabled={updateMutation.isPending}
            data-testid="button-save-placeholders"
          >
            <Save className="mr-2 h-4 w-4" />
            {updateMutation.isPending ? "Menyimpan..." : "Simpan"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/admin/prize-manage.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Prize } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Plus, Edit2, Trash2, Save, Upload, Link } from "lucide-react";

export default function PrizeManagement() {
  const { toast } = useToast();
  const [isCreateModalOpen, setIsCreateModalOpen] = useState(false);
  const [editingPrize, setEditingPrize] = useState<Prize | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    couponNumber: "",
    description: "",
    bannerUrl: "",
    startDate: "",
    endDate: "",
    isActive: true,
  });
  const [bannerFile, setBannerFile] = useState<File | null>(null);
  const [bannerPreview, setBannerPreview] = useState<string>("");

  const { data: prizes = [], isLoading } = useQuery<Prize[]>({
    queryKey: ["/api/prizes"],
  });

  const createPrizeMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      const res = await apiRequest("POST", "/api/prizes", {
        ...data,
        startDate: new Date(data.startDate).toISOString(),
        endDate: new Date(data.endDate).toISOString(),
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/prizes"] });
      setIsCreateModalOpen(false);
      resetForm();
      toast({
        title: "Berhasil",
        description: "Hadiah berhasil ditambahkan",
      });
    },
    onError: (error) => {
      toast({
        title: "Kesalahan",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const updatePrizeMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<typeof formData> }) => {
      const updateData = { ...data };
      if (data.startDate) updateData.startDate = new Date(data.startDate).toISOString();
      if (data.endDate) updateData.endDate = new Date(data.endDate).toISOString();

      const res = await apiRequest("PUT", `/api/prizes/${id}`, updateData);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/prizes"] });
      setEditingPrize(null);
      toast({
        title: "Berhasil",
        description: "Hadiah berhasil diperbarui",
      });
    },
    onError: (error) => {
      toast({
        title: "Kesalahan",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deletePrizeMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/prizes/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/prizes"] });
      toast({
        title: "Berhasil",
        description: "Hadiah berhasil dihapus",
      });
    },
    onError: (error) => {
      toast({
        title: "Kesalahan",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      couponNumber: "",
      description: "",
      bannerUrl: "",
      startDate: "",
      endDate: "",
      isActive: true,
    });
    setBannerFile(null);
    setBannerPreview("");
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setBannerFile(file);
      const reader = new FileReader();
      reader.onload = (e) => {
        setBannerPreview(e.target?.result as string);
        setFormData(prev => ({ ...prev, bannerUrl: e.target?.result as string }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleCreate = (e: React.FormEvent) => {
    e.preventDefault();
    createPrizeMutation.mutate(formData);
  };

  const handleEdit = (prize: Prize) => {
    setEditingPrize(prize);
  };

  const handleUpdate = (field: string, value: any) => {
    if (editingPrize) {
      const updatedData = { [field]: value };
      updatePrizeMutation.mutate({
        id: editingPrize.id,
        data: updatedData,
      });
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Apakah Anda yakin ingin menghapus hadiah ini?")) {
      deletePrizeMutation.mutate(id);
    }
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleString("id-ID");
  };

  const formatDateForInput = (date: Date | string) => {
    return new Date(date).toISOString().slice(0, 16);
  };

  if (isLoading) {
    return <div>Memuat hadiah...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-lg font-semibold">Kelola Hadiah</h2>
          <p className="text-gray-600 text-sm">Tambah, edit, dan hapus hadiah undian</p>
        </div>
        <Dialog open={isCreateModalOpen} onOpenChange={setIsCreateModalOpen}>
          <DialogTrigger asChild>
            <Button className="bg-brand-green hover:bg-brand-green-dark">
              <Plus className="h-4 w-4 mr-2" />
              Tambah Hadiah
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Tambah Hadiah Baru</DialogTitle>
            </DialogHeader>
            <form onSubmit={handleCreate} className="space-y-4">
              <div>
                <Label>Nama Hadiah</Label>
                <Input
                  value={formData.name}
                  onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                  placeholder="Contoh: TUMBLR"
                  required
                  data-testid="input-prize-name"
                />
              </div>

              <div>
                <Label>Nomor Kupon</Label>
                <Input
                  value={formData.couponNumber}
                  onChange={(e) => setFormData(prev => ({ ...prev, couponNumber: e.target.value }))}
                  placeholder="Contoh: TUM001"
                  required
                  data-testid="input-coupon-number"
                />
              </div>

              <div>
                <Label>Deskripsi</Label>
                <Textarea
                  value={formData.description}
                  onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                  placeholder="Deskripsi hadiah..."
                  data-testid="textarea-description"
                />
              </div>

              <div>
                <Label>Banner Hadiah</Label>
                <Tabs defaultValue="upload" className="w-full">
                  <TabsList className="grid w-full grid-cols-2">
                    <TabsTrigger value="upload" className="flex items-center gap-2">
                      <Upload className="h-4 w-4" />
                      Upload File
                    </TabsTrigger>
                    <TabsTrigger value="url" className="flex items-center gap-2">
                      <Link className="h-4 w-4" />
                      URL Link
                    </TabsTrigger>
                  </TabsList>

                  <TabsContent value="upload" className="space-y-2">
                    <Input
                      type="file"
                      accept="image/*"
                      onChange={handleFileChange}
                      data-testid="input-banner-file"
                    />
                    {bannerPreview && (
                      <div className="mt-2">
                        <img 
                          src={bannerPreview} 
                          alt="Preview banner" 
                          className="w-full h-32 object-cover rounded border"
                        />
                      </div>
                    )}
                  </TabsContent>

                  <TabsContent value="url" className="space-y-2">
                    <Input
                      type="url"
                      value={formData.bannerUrl}
                      onChange={(e) => {
                        setFormData(prev => ({ ...prev, bannerUrl: e.target.value }));
                        setBannerFile(null);
                        setBannerPreview("");
                      }}
                      placeholder="https://example.com/banner.png"
                      data-testid="input-banner-url"
                    />
                    {formData.bannerUrl && (
                      <div className="mt-2">
                        <img 
                          src={formData.bannerUrl} 
                          alt="Preview banner" 
                          className="w-full h-32 object-cover rounded border"
                          onError={(e) => {
                            e.currentTarget.src = "data:image/svg+xml;base64,";
                          }}
                        />
                      </div>
                    )}
                  </TabsContent>
                </Tabs>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Tanggal Mulai</Label>
                  <Input
                    type="datetime-local"
                    value={formData.startDate}
                    onChange={(e) => setFormData(prev => ({ ...prev, startDate: e.target.value }))}
                    required
                  />
                </div>
                <div>
                  <Label>Tanggal Selesai</Label>
                  <Input
                    type="datetime-local"
                    value={formData.endDate}
                    onChange={(e) => setFormData(prev => ({ ...prev, endDate: e.target.value }))}
                    required
                  />
                </div>
              </div>

              <div className="flex gap-3">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsCreateModalOpen(false)}
                  className="flex-1"
                >
                  Batal
                </Button>
                <Button
                  type="submit"
                  className="flex-1 bg-brand-green hover:bg-brand-green-dark"
                  disabled={createPrizeMutation.isPending}
                >
                  {createPrizeMutation.isPending ? "Menambah..." : "Tambah Hadiah"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="bg-white rounded-lg border overflow-hidden">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Nama Hadiah</TableHead>
              <TableHead>Nomor Kupon</TableHead>
              <TableHead>Banner</TableHead>
              <TableHead>Tanggal Mulai</TableHead>
              <TableHead>Tanggal Selesai</TableHead>
              <TableHead>Status</TableHead>
              <TableHead>Aksi</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {prizes.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                  Belum ada hadiah. Tambah hadiah pertama Anda.
                </TableCell>
              </TableRow>
            ) : (
              prizes.map((prize) => (
                <TableRow key={prize.id}>
                  <TableCell>
                    {editingPrize?.id === prize.id ? (
                      <Input
                        defaultValue={prize.name}
                        onBlur={(e) => handleUpdate("name", e.target.value)}
                        className="max-w-32"
                        data-testid={`input-edit-name-${prize.id}`}
                      />
                    ) : (
                      <span className="font-medium">{prize.name}</span>
                    )}
                  </TableCell>
                  <TableCell>
                    {editingPrize?.id === prize.id ? (
                      <Input
                        defaultValue={prize.couponNumber}
                        onBlur={(e) => handleUpdate("couponNumber", e.target.value)}
                        className="max-w-32"
                        data-testid={`input-edit-coupon-${prize.id}`}
                      />
                    ) : (
                      <span className="font-mono font-medium text-blue-600">{prize.couponNumber}</span>
                    )}
                  </TableCell>
                  <TableCell>
                    {prize.bannerUrl ? (
                      <img src={prize.bannerUrl} alt="Banner" className="w-12 h-8 object-cover rounded" />
                    ) : (
                      <div className="w-12 h-8 bg-gray-200 rounded flex items-center justify-center text-xs">
                        No Banner
                      </div>
                    )}
                  </TableCell>
                  <TableCell>
                    {editingPrize?.id === prize.id ? (
                      <Input
                        type="datetime-local"
                        defaultValue={formatDateForInput(prize.startDate)}
                        onBlur={(e) => handleUpdate("startDate", e.target.value)}
                        className="max-w-40"
                      />
                    ) : (
                      <span className="text-sm">{formatDate(prize.startDate)}</span>
                    )}
                  </TableCell>
                  <TableCell>
                    {editingPrize?.id === prize.id ? (
                      <Input
                        type="datetime-local"
                        defaultValue={formatDateForInput(prize.endDate)}
                        onBlur={(e) => handleUpdate("endDate", e.target.value)}
                        className="max-w-40"
                      />
                    ) : (
                      <span className="text-sm">{formatDate(prize.endDate)}</span>
                    )}
                  </TableCell>
                  <TableCell>
                    <Badge variant={prize.isActive ? "default" : "secondary"}>
                      {prize.isActive ? "Aktif" : "Nonaktif"}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <div className="flex gap-2">
                      {editingPrize?.id === prize.id ? (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => setEditingPrize(null)}
                        >
                          <Save className="h-4 w-4" />
                        </Button>
                      ) : (
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleEdit(prize)}
                        >
                          <Edit2 className="h-4 w-4" />
                        </Button>
                      )}
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleDelete(prize.id)}
                        className="text-red-600 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>
    </div>
  );
}

client/src/components/admin/profile-sett.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Settings } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { Upload, Save } from "lucide-react";

export default function ProfileSettings() {
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    siteTitle: "",
    logoUrl: "",
    bannerUrl: "",
    adminWhatsApp: "",
    mapsLink: "",
    termsAndConditions: "",
    winnerMessage: "",
  });

  const { data: settings, isLoading } = useQuery<Settings>({
    queryKey: ["/api/settings"],
  });

  useEffect(() => {
    if (settings) {
      setFormData({
        siteTitle: settings.siteTitle || "",
        logoUrl: settings.logoUrl || "",
        bannerUrl: settings.bannerUrl || "",
        adminWhatsApp: settings.adminWhatsApp || "",
        mapsLink: settings.mapsLink || "",
        termsAndConditions: settings.termsAndConditions || "",
        winnerMessage: settings.winnerMessage || "",
      });
    }
  }, [settings]);

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      const res = await apiRequest("PUT", "/api/settings", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
      toast({
        title: "Berhasil",
        description: "Pengaturan telah disimpan",
      });
    },
    onError: (error) => {
      toast({
        title: "Kesalahan",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleFileUpload = (field: 'logoUrl' | 'bannerUrl') => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'image/*';
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        // Check file size (limit to 5MB)
        if (file.size > 5 * 1024 * 1024) {
          toast({
            title: "File Terlalu Besar",
            description: "Ukuran file maksimal 5MB. Silakan kompres gambar terlebih dahulu.",
            variant: "destructive",
          });
          return;
        }

        const reader = new FileReader();
        reader.onload = () => {
          setFormData(prev => ({ ...prev, [field]: reader.result as string }));
        };
        reader.onerror = () => {
          toast({
            title: "Gagal Memuat File",
            description: "Terjadi kesalahan saat memuat file. Silakan coba lagi.",
            variant: "destructive",
          });
        };
        reader.readAsDataURL(file);
      }
    };
    input.click();
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateSettingsMutation.mutate(formData);
  };

  if (isLoading) {
    return <div>Memuat pengaturan...</div>;
  }

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <Label htmlFor="siteTitle">Judul Website</Label>
          <Input
            id="siteTitle"
            value={formData.siteTitle}
            onChange={(e) => setFormData(prev => ({ ...prev, siteTitle: e.target.value }))}
            placeholder="Cek Kupon Undian"
          />
        </div>

        <div>
          <Label htmlFor="adminWhatsApp">WhatsApp Admin</Label>
          <Input
            id="adminWhatsApp"
            value={formData.adminWhatsApp}
            onChange={(e) => setFormData(prev => ({ ...prev, adminWhatsApp: e.target.value }))}
            placeholder="wa.me/62651465165651"
          />
        </div>
      </div>

      <div>
        <Label htmlFor="mapsLink">Link Peta Lokasi</Label>
        <Input
          id="mapsLink"
          type="url"
          value={formData.mapsLink}
          onChange={(e) => setFormData(prev => ({ ...prev, mapsLink: e.target.value }))}
          placeholder="https://maps.google.com/..."
        />
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <Label>Logo</Label>
          <div className="space-y-2">
            <div className="flex gap-2">
              <Input
                type="url"
                value={formData.logoUrl}
                onChange={(e) => setFormData(prev => ({ ...prev, logoUrl: e.target.value }))}
                placeholder="https://example.com/logo.png"
                className="flex-1"
              />
              <Button
                type="button"
                variant="outline"
                onClick={() => handleFileUpload('logoUrl')}
              >
                <Upload className="h-4 w-4" />
              </Button>
            </div>
            {formData.logoUrl && (
              <div className="mt-2">
                <img src={formData.logoUrl} alt="Logo Preview" className="w-16 h-16 object-cover rounded" />
              </div>
            )}
          </div>
        </div>

        <div>
          <Label>Banner</Label>
          <div className="space-y-2">
            <div className="flex gap-2">
              <Input
                type="url"
                value={formData.bannerUrl}
                onChange={(e) => setFormData(prev => ({ ...prev, bannerUrl: e.target.value }))}
                placeholder="https://example.com/banner.png"
                className="flex-1"
              />
              <Button
                type="button"
                variant="outline"
                onClick={() => handleFileUpload('bannerUrl')}
              >
                <Upload className="h-4 w-4" />
              </Button>
            </div>
            {formData.bannerUrl && (
              <div className="mt-2">
                <img src={formData.bannerUrl} alt="Banner Preview" className="w-32 h-20 object-cover rounded" />
              </div>
            )}
          </div>
        </div>
      </div>

      <div>
        <Label htmlFor="winnerMessage">Pesan Pemenang</Label>
        <Input
          id="winnerMessage"
          value={formData.winnerMessage}
          onChange={(e) => setFormData(prev => ({ ...prev, winnerMessage: e.target.value }))}
          placeholder="Anda mendapatkan hadiah dari ConnectPrinting, segera tukarkan :)"
        />
      </div>

      <div>
        <Label htmlFor="termsAndConditions">Syarat & Ketentuan</Label>
        <Textarea
          id="termsAndConditions"
          rows={8}
          value={formData.termsAndConditions}
          onChange={(e) => setFormData(prev => ({ ...prev, termsAndConditions: e.target.value }))}
          placeholder="Cara Klaim Hadiahmu..."
        />
      </div>

      <Button
        type="submit"
        className="bg-brand-green hover:bg-brand-green-dark"
        disabled={updateSettingsMutation.isPending}
      >
        <Save className="h-4 w-4 mr-2" />
        {updateSettingsMutation.isPending ? "Menyimpan..." : "Simpan Perubahan"}
      </Button>
    </form>
  );
}

client/src/components/admin/submission-h.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Submission } from "@shared/schema";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { History, Search, CheckCircle, XCircle, Calendar, User, Phone, Hash } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import { id } from "date-fns/locale";

export default function SubmissionHistory() {
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredSubmissions, setFilteredSubmissions] = useState<Submission[]>([]);

  const { data: submissions = [], isLoading } = useQuery<Submission[]>({
    queryKey: ["/api/submissions"],
  });

  useEffect(() => {
    if (!submissions) return;

    const filtered = submissions.filter(submission => 
      submission.couponNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
      submission.fullName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      submission.whatsappNumber.includes(searchQuery) ||
      (submission.prizeName && submission.prizeName.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    setFilteredSubmissions(filtered);
  }, [submissions, searchQuery]);

  if (isLoading) {
    return <div>Memuat riwayat...</div>;
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center">
          <History className="mr-2 h-5 w-5" />
          Riwayat Pengecekan Kupon
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="mb-4">
          <div className="relative">
            <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Cari berdasarkan nomor kupon, nama, WhatsApp, atau hadiah..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
              data-testid="input-search-submissions"
            />
          </div>
        </div>

        <div className="space-y-4">
          {filteredSubmissions.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              {searchQuery ? "Tidak ada hasil yang ditemukan" : "Belum ada riwayat pengecekan kupon"}
            </div>
          ) : (
            filteredSubmissions.map((submission) => (
              <Card key={submission.id} className="border-l-4" style={{
                borderLeftColor: submission.isWinner ? '#10b981' : '#6b7280'
              }}>
                <CardContent className="p-4">
                  <div className="flex items-center justify-between mb-3">
                    <div className="flex items-center space-x-2">
                      {submission.isWinner ? (
                        <CheckCircle className="h-5 w-5 text-green-500" />
                      ) : (
                        <XCircle className="h-5 w-5 text-gray-500" />
                      )}
                      <Badge variant={submission.isWinner ? "default" : "secondary"}>
                        {submission.isWinner ? "MENANG" : "TIDAK MENANG"}
                      </Badge>
                    </div>
                    <div className="flex items-center text-sm text-gray-500">
                      <Calendar className="h-4 w-4 mr-1" />
                      {formatDistanceToNow(new Date(submission.submittedAt!), { 
                        addSuffix: true,
                        locale: id 
                      })}
                    </div>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    <div className="flex items-center">
                      <Hash className="h-4 w-4 mr-2 text-gray-400" />
                      <div>
                        <p className="text-sm text-gray-500">Nomor Kupon</p>
                        <p className="font-mono font-medium">{submission.couponNumber}</p>
                      </div>
                    </div>

                    <div className="flex items-center">
                      <User className="h-4 w-4 mr-2 text-gray-400" />
                      <div>
                        <p className="text-sm text-gray-500">Nama Lengkap</p>
                        <p className="font-medium">{submission.fullName}</p>
                      </div>
                    </div>

                    <div className="flex items-center">
                      <Phone className="h-4 w-4 mr-2 text-gray-400" />
                      <div>
                        <p className="text-sm text-gray-500">WhatsApp</p>
                        <p className="font-medium">{submission.whatsappNumber}</p>
                      </div>
                    </div>

                    {submission.isWinner && submission.prizeName && (
                      <div className="flex items-center">
                        <CheckCircle className="h-4 w-4 mr-2 text-green-500" />
                        <div>
                          <p className="text-sm text-gray-500">Hadiah</p>
                          <p className="font-medium text-green-600">{submission.prizeName}</p>
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            ))
          )}
        </div>

        {filteredSubmissions.length > 0 && (
          <div className="mt-4 text-center text-sm text-gray-500">
            Menampilkan {filteredSubmissions.length} dari {submissions.length} total entri
          </div>
        )}
      </CardContent>
    </Card>
  );
}

